import React from 'react';
import Role from './Elements/Role';

class ChoiceRole extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            show: true,
            info_player: [
                {
                    text_recommend: 'Rekomenduojame',
                    player: {
                        name: "Los Santos",
                        text: 'Lėktuvu iš savo šalies imigruojate į Los Santosą, kur su būtiniausiais daiktais, kuriuos pavyko pasiimti su savimi, jūsų laukia darbas ir naujų aukštumų siekimas.'
                    }
                },
                {
                    text_recommend: '',
                    player: {
                        name: "Cayo Perico",
                        text: 'Atvykę lėktuvu į Cayo Perico, tvarkingai apsirengę, su pirmaisiais pinigais ir telefonu, visi keliai yra atviri, kad galėtumėte užkariauti pasaulį!'
                    }
                },
                {
                    text_recommend: '',
                    player: {
                        name: "Smėlio pakrantės",
                        text: 'Atsibudęs Blaino apygardos priemiestyje, apsirengęs senais purvinais drabužiais ir be cento kišenėje, turi grįžti į įprastą gyvenimą. Gerai tai, kad jau esate visateisis JAV pilietis, todėl jūsų kelionė į priekį bus lengvesnė.'
                    }
                }
            ]
        }
    }

    componentDidCatch(error, errorInfo) {
        mp.trigger('client:ui:debug', 'ChoiceRole.jsx', error, errorInfo); // eslint-disable-line
    }

    render() {
        return (
            <React.Fragment>
                <div className="reg-main adaptive-reg ch-role">
                    <div className="create-content">
                        <div className="main-box-change">
                            <Role
                                index={1}
                                text={this.state.info_player[1].text_recommend}
                                name={this.state.info_player[1].player.name}
                                info_player={this.state.info_player}
                                text_info={this.state.info_player[1].player.text}
                            />
                            <Role
                                index={0}
                                text={this.state.info_player[0].text_recommend}
                                name={this.state.info_player[0].player.name}
                                info_player={this.state.info_player}
                                text_info={this.state.info_player[0].player.text}
                            />
                            <Role
                                index={2}
                                text={this.state.info_player[2].text_recommend}
                                name={this.state.info_player[2].player.name}
                                info_player={this.state.info_player}
                                text_info={this.state.info_player[2].player.text}
                            />
                        </div>
                    </div>
                </div>

            </React.Fragment>
        )
    }
}

export default ChoiceRole;
